mapper.select:
SELECT M1.M_NO, M1.M_ID, M1.M_NM, M1.M_PHONE FROM (SELECT M_NO, M_ID, M_NM, M_PHONE, ROW_NUMBER() OVER(ORDER BY M_NO DESC) AS RNUM FROM M1 WHERE M1.M_DEL = 1
AND M1.M_NM LIKE '%' || #{searchTxt} || '%'
AND M1.M_ID LIKE '%' || #{searchTxt} || '%'
) M1 WHERE M1.RNUM BETWEEN #{startCnt} AND #{endCnt}

mapper.update:
UPDATE M1 SET M_NM = #{m_nm},

		 M_PW = #{m_pw},

         M_PHONE = #{m_phone}
WHERE M_NO = #{m_no}

mapper.select:
SELECT COUNT(*) AS CNT
FROM M1
WHERE M_ID = #{m_id}

SELECT M_NO, M_ID, M_NM, M_PHONE, TO_CHAR(M_JOIN, 'YYYY-MM-DD') AS M_JOIN
FROM M1
WHERE M_NO = #{m_no}
